package Lec1;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 973;
		if (n > 657) {
			System.out.println("hey");
		} 
		if (n % 2 == 0) {// yes
			System.out.println("Even");

		} else {// No
			System.out.println("Odd");
		}

	}

}
